mapper.update:
UPDATE Info
SET replyCnt = replyCnt + 1
WHERE boardNum = #{boardNum}

UPDATE Info
SET replyCnt = replyCnt - 1
WHERE boardNum = #{boardNum}

UPDATE Info
SET boardHit = boardHit + 1
WHERE boardNum = #{boardNum}

UPDATE Info
SET boardLike = boardLike + 1
WHERE boardNum = #{boardNum}

UPDATE Info
SET boardLike = boardLike - 1
WHERE boardNum = #{boardNum}
