mapper.update:
UPDATE Petsitter
SET replyCnt = replyCnt + 1
WHERE boardNum = #{boardNum}

UPDATE Petsitter
SET replyCnt = replyCnt - 1
WHERE boardNum = #{boardNum}

UPDATE Petsitter
SET boardHit = boardHit + 1
WHERE boardNum = #{boardNum}

mapper.select:
SELECT *
FROM Petsitter
WHERE petPrice = #{petPrice}, petLocal = #{petLocal}, petDay = #{petDay}, petSize = #{petSize}

mapper.update:
UPDATE Petsitter
SET boardLike = boardLike + 1
WHERE boardNum = #{boardNum}

UPDATE Petsitter
SET boardLike = boardLike - 1
WHERE boardNum = #{boardNum}

UPDATE Petsitter
SET boardLike = #{boardLike}
WHERE boardNum = #{boardNum}
