mapper.select:
SELECT *
FROM UserList
WHERE userID = #{userID} AND pwd = #{pwd}
LIMIT 1;

SELECT COUNT(*)
FROM userList
WHERE userID = #{userID}

SELECT authority
FROM userList
WHERE userID = #{userID}

mapper.update:
UPDATE UserList SET  
email = #{email},  
pwd = #{pwd},  
userType = #{userType}  
WHERE userID = #{userID}

mapper.select:
SELECT userType
FROM userList
WHERE userID = #{userID}
